// poi-filters.js

$( document ).ready(function() {

	// listener for "eat" filter in poi radar view
	$("#filter-food").click(function() {
		$(".filterbutton").removeClass("active");
		$("#filter-food").addClass("active");
		filterPois("food");
	});

	// listener for "drink" filter in poi radar view
	$("#filter-drink").click(function() {
		$(".filterbutton").removeClass("active");
		$("#filter-drink").addClass("active");
		filterPois("bar");
	});

	// listener for "play" filter in poi radar view
	$("#filter-play").click(function() {
		$(".filterbutton").removeClass("active");
		$("#filter-play").addClass("active");
		filterPois("park");
	});

	// listener for "all" filter in poi radar view
	$("#filter-all").click(function() {
		$(".filterbutton").removeClass("active");
		$("#filter-all").addClass("active");
		filterPois(null);
	});

});


function filterPois(filter) {
    $(".poi-radar-item").css("display", "block");
	if (filter !== null) {
	    $(".poi-radar-item:not(:contains('" + filter + "'))").css("display", "none");
	}
}
// Sets the map on all markers in the array.
function setAllMap(map, markers) {
  for (var i = 0; i < markers.length; i++) {
    markers[i].setMap(map);
  }
}

// Removes the markers from the map, but keeps them in the array.
function clearMarkers(markers) {
  setAllMap(null, markers);
  console.debug("clearing markers");
}

// function to return a random member of a given array
function randomFromArray(array) {
    return array[Math.floor(Math.random() * array.length)];
}

function calcDistance (fromLat, fromLng, toLat, toLng) {
	return google.maps.geometry.spherical.computeDistanceBetween(
	new google.maps.LatLng(fromLat, fromLng), new google.maps.LatLng(toLat, toLng));
}
// functions for dealing with places of interest


// function to add a poi to the poi radar list window
function addPoi(poi) {
    // make the marker

    // call google places to get the place ID
    // callbacks from getPlaceId go on complete the process
    getPlaceId(poi);

    // temporarily skip the google places jazz and add a mock place to the list
    // addPlaceDetails(mockPlace(poi.name));
}



// function to search for a place based on name and lat/lng
// calls getPlaceInfo
function getPlaceId(place) {
    var placeLoc = new google.maps.LatLng(place.lat, place.lng);
    var request = {
        location: placeLoc,
        radius: 500,
        query: place.name
    };
    placesService.textSearch(request, function(results, status) {
        if (status == google.maps.places.PlacesServiceStatus.OK) {
            // get place info
            getPlaceInfo(results[0].place_id);
        } else {
            console.debug("place ID lookup error:");
            console.debug(status);
            console.debug("failed request: ");
            console.debug(request);
        }

    });
}
// function to get info about a place
// called by getPlaceId
function getPlaceInfo(placeId) {
    var request = {
        placeId: placeId
    };

    // stagger details request from id request to avoid query-per-second limit. See comments in poi.js.
    setTimeout(function() {
        placesService.getDetails(request, function(place, status) {
            if (status == google.maps.places.PlacesServiceStatus.OK) {
                addPlaceDetails(place);
            } else {
                console.debug("place detail lookup error:")
                console.debug(status);
                console.debug("failed request: ");
                console.debug(request);
            }
        });
    }, urlOptions.queryDelay);

}

// function to add a place to the radar list
// supposed to be called by getPlaceInfo
// currently called by addPoi
function addPlaceDetails(place) {
    // make poi marker
    makePoiMarker(place);


    // create the tooltip
    setTimeout(function() {
        $("#" + place.anchor + "-marker").tooltip({
            title: place.name,
            trigger: 'manual'
        });
    }, 100);

    // extract the place image
    if (typeof place.photos !== 'undefined') {
        place.imageUrl = place.photos[0].getUrl({maxWidth: 500, maxHeight: 500});
    }



    // preapre a formatted shortname
    place.anchor = place.name.replace(/\s+/g, '').toLowerCase().replace(/\W/g, '');

    // render it into the template
    var source = $("#poi-template").html();
    var template = Handlebars.compile(source);
    var html = template(place);
    // inject it into the list
    $("#poi-radar-list").append(html);
    console.debug (place.rating);
    if(typeof place.rating !== "undefined") {
        // make the star rating
        $(".rateme").raty({
            score: place.rating,
            readOnly: true
        });
    }
    // and then untag the rateme
    $(".rateme").removeClass("rateme");
}


// function to create a marker for a poi, complete with a tooltip and link to the radar drawer
function makePoiMarker(poi) {

    // create the marker object
    marker = new google.maps.Marker({
        position: poi.geometry.location,
        map: map,
        title: poi.name,
        icon: {
            scaledSize: new google.maps.Size(iconSize.width, iconSize.height),
            url: poi.icon
        }
    });

    // create a second invisible marker object to hold the tooltip
    // but first make an anchor
    poi.anchor = poi.name.replace(/\s+/g, '').toLowerCase().replace(/\W/g, '');
    marker2 = new RichMarker({
        position: poi.geometry.location,
        flat: true,
        map: map,
        title: poi.name,
        draggable: false,
        content: '<div style="width: ' + iconSize.width + 'px; height: ' + iconSize.width + 'px" id="' + poi.anchor + '-marker">&nbsp;</div>'
    });

    // listener for mouseover
    google.maps.event.addListener(marker, 'mouseover', function() {
        $("#" + poi.anchor + "-marker").tooltip('show');
    });

    // listener for mouseoff
    google.maps.event.addListener(marker, 'mouseout', function() {
        $("#" + poi.anchor + "-marker").tooltip('hide');
    });

    // listener to open the poi radar if marker is clicked
    google.maps.event.addListener(marker, 'click', function() {
        snapper.open('left');
        window.location.hash = poi.anchor;
    });


    poiMarkers.push(marker);
    poiRichMarkers.push(marker2);
}


