// controller for radar button

function radarControl() {


    // create Radar control div

    // Create the DIV to hold the control and
    // call the RadarControl() constructor passing
    // in this DIV.
    var radarControlDiv = document.createElement('div');
    radarControlDiv.id = "slider-button"
    var radarControl = new RadarControl(radarControlDiv, map);

    radarControlDiv.index = 1;
    map.controls[google.maps.ControlPosition.TOP_RIGHT].push(radarControlDiv);


}





/**
 * The RadarControl adds a control to the map that Radars the current Radar state
 */

function RadarControl(controlDiv, map) {

    // Set CSS styles for the DIV containing the control
    // Setting padding to 5 px will offset the control
    // from the edge of the map
    controlDiv.style.padding = '5px';

    // Set CSS for the control border
    var controlUI = document.createElement('div');
    controlUI.className = "map-button-active";
    controlUI.title = 'Nearby Points Of Interest';
    controlDiv.appendChild(controlUI);

    // Set CSS for the control interior
    var controlText = document.createElement('div');
    controlText.innerHTML = 'Point Of Interest Radar';
    controlUI.appendChild(controlText);

    // Setup the click event listeners
    google.maps.event.addDomListener(controlUI, 'click', function() {
        snapper.open('left');
    });

}

// controller for directions button

function directionsControl() {


    // create Directions control div

    // Create the DIV to hold the control and
    // call the DirectionsControl() constructor passing
    // in this DIV.
    var directionsControlDiv = document.createElement('div');
    directionsControlDiv.id = "slider-button"
    var directionsControl = new DirectionsControl(directionsControlDiv, map);

    directionsControlDiv.index = 1;
    map.controls[google.maps.ControlPosition.TOP_RIGHT].push(directionsControlDiv);


}





/**
 * The DirectionsControl adds a control to the map that Directionss the current Directions state
 */

function DirectionsControl(controlDiv, map) {

    // Set CSS styles for the DIV containing the control
    // Setting padding to 5 px will offset the control
    // from the edge of the map
    controlDiv.style.padding = '5px';

    // Set CSS for the control border
    var controlUI = document.createElement('div');
    controlUI.className = "map-button-inactive";
    controlUI.title = 'Directions';
    controlDiv.appendChild(controlUI);

    // Set CSS for the control interior
    var controlText = document.createElement('div');
    controlText.innerHTML = 'Directions';
    controlUI.appendChild(controlText);

    // Setup the click event listeners
    google.maps.event.addDomListener(controlUI, 'click', function() {
        snapper.open('right');
    });

}

