function calcRoute(origin, destination, datag) {
	// create route request
	var request = {
		origin: origin,
		destination: destination,
		travelMode: google.maps.TravelMode.DRIVING
	};
	console.log("about to make directions request");
    console.log(request);
	directionsService.route(request, function(response, status) {
		if (status == google.maps.DirectionsStatus.OK) {
            directionsDisplay.setDirections(response);
            var bounds = new google.maps.LatLngBounds();
            var route = response.routes[0];
            startLocation = new Object();
            endLocation = new Object();

            // For each route, display summary information.
            var path = response.routes[0].overview_path;
            var legs = response.routes[0].legs;
            for (i = 0; i < legs.length; i++) {
                if (i == 0) {
                    startLocation.latlng = legs[i].start_location;
                    startLocation.address = legs[i].start_address;
                    // travelMarker = google.maps.Marker({map:map,position: startLocation.latlng});
                    travelMarker = createMarker(legs[i].start_location, "start", legs[i].start_address, "green");
                }
                endLocation.latlng = legs[i].end_location;
                endLocation.address = legs[i].end_address;
                var steps = legs[i].steps;
                for (j = 0; j < steps.length; j++) {
                    var nextSegment = steps[j].path;
                    for (k = 0; k < nextSegment.length; k++) {
                        polyline.getPath().push(nextSegment[k]);
                        bounds.extend(nextSegment[k]);



                    }
                }
            }

            polyline.setMap(map);
            map.fitBounds(bounds);
            //        createMarker(endLocation.latlng,"end",endLocation.address,"red");
            map.setZoom(18);

		} else {
			console.debug(status);
		}

	});
}

function getJSONdata() {
	var requestUrl = "http://b.ss.cx/scottstreit.com:8080/infer/rest/listTracksNextEvent?name=" + urlOptions.name + "&email=" + urlOptions.email + "&poisPerPoint=" + urlOptions.poisPerPoint + "&iter=" + urlOptions.iter;
	// var requestUrl = "directions2.json";
	console.log(requestUrl);
	$.getJSON(requestUrl, function(data) {
		// log
		console.log(data);

        // extract stepDistances
        var runningTotal = 0;
        $.each(data.routes[0].legs[0].steps, function(key, value) {
            runningTotal = runningTotal + value.distance.value;
            stepDistances.push(runningTotal);
        });

        // console.log(stepDistances);

		// extract start and end
		var originPoint = new google.maps.LatLng(data.routes[0].legs[0].start_location.lat, data.routes[0].legs[0].start_location.lng);
		destinationPoint = new google.maps.LatLng(data.routes[0].legs[0].end_location.lat, data.routes[0].legs[0].end_location.lng);

		// call route function
		calcRoute(originPoint, destinationPoint, data);


		// do the POIs
        // NOTE TYPO (from server): "inffered"
		$.each(data.infferedPOIs, function(key, value) {
			// console.log(value);
            setTimeout(function() {
                addPoi(value);
            }, urlOptions.queryDelay * key * 2); // * 2 because each poi needs two requests
            if(urlOptions.removePois === "true") {
                poiRemovalLocations.push({actLat: value.actLat, actLng: value.actLng, name: value.name, lat: value.lat, lng: value.lng});
            }
		});
        if (urlOptions.removePois === "true") {
            setTimeout(function() {
                console.log("starting animation!");
                startAnimation();
            }, urlOptions.queryDelay * 2 * ( data.infferedPOIs.length + 2));
        } else {
            startAnimation();
        };
	});

}




//=============== animation functions ======================

function updatePoly(d) {
    // Spawn a new polyline every 20 vertices, because updating a 100-vertex poly is too slow
    if (poly2.getPath().getLength() > 20) {
        poly2 = new google.maps.Polyline([polyline.getPath().getAt(lastVertex - 1)]);
        // map.addOverlay(poly2)
    }

    if (polyline.GetIndexAtDistance(d) < lastVertex + 2) {
        if (poly2.getPath().getLength() > 1) {
            poly2.getPath().removeAt(poly2.getPath().getLength() - 1)
        }
        poly2.getPath().insertAt(poly2.getPath().getLength(), polyline.GetPointAtDistance(d));
    } else {
        poly2.getPath().insertAt(poly2.getPath().getLength(), endLocation.latlng);
    }
}


function animate(d) {
    if (d > stepDistances[0]) {
        // delete first directions step
        $("tbody").find('tr:first').remove();
        stepDistances.shift();
    };
    // alert("animate("+d+")");
    if (d > eol) {
        // map.panTo(endLocation.latlng);
        travelMarker.setPosition(endLocation.latlng);
        return;
    }
    var p = polyline.GetPointAtDistance(d);
    // each frame of animation, check to see if you're passing a poi. If so, remove it from the map.
    if(urlOptions.removePois === "true") {
        // go through each poi
        $.each(poiRemovalLocations, function(key, value) {
            if (typeof value !== "undefined" && typeof p !== "undefined" && p !== null) {
                // console.log(p);
                // check if you're passing one
                if(calcDistance(value.actLat, value.actLng, p.lat(), p.lng() ) <= urlOptions.step) {
                    // if so, find the right marker
                    for (var i = poiRichMarkers.length - 1; i >= 0; i--) {
                        if(poiRichMarkers[i].getPosition().lat().toFixed(6) == value.lat && poiRichMarkers[i].getPosition().lng().toFixed(6) == value.lng) {
                            // and delete it
                            poiRichMarkers[i].setMap(null);
                        }
                    };
                    for (var i = poiMarkers.length - 1; i >= 0; i--) {
                        if(poiMarkers[i].getPosition().lat().toFixed(6) == value.lat && poiRichMarkers[i].getPosition().lng().toFixed(6) == value.lng) {
                            poiMarkers[i].setMap(null);
                        }
                    };
                }
            }
        });
    }

    // map.panTo(p);
    travelMarker.setPosition(p);
    updatePoly(d);
    if (p === null) {
        urlOptions.iter ++;
        var newUrl = window.location.href.split('?')[0] + "?iter=" + urlOptions.iter + "&queryDelay=" + urlOptions.queryDelay + "&step=" + urlOptions.step + "&tick=" + urlOptions.tick + "&poisPerPoint=" + urlOptions.poisPerPoint + "&removePois=" + urlOptions.removePois + "&name=" + urlOptions.name + "&email" + urlOptions.email;
        console.log(newUrl);
        window.location = newUrl;
    };
    timerHandle = setTimeout("animate(" + (d + urlOptions.step) + ")", urlOptions.tick);        
}


